import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { data } from 'jquery';
import { Producto } from 'src/app/Modelo/Producto';
import { Usuario } from 'src/app/Modelo/Usuario';
import { ProductosService } from 'src/app/Service/productos.service';
import { ProductosSucursales } from 'src/app/Modelo/ProductosSucursales';

@Component({
  selector: 'app-add-productos-bodega',
  templateUrl: './add-productos-bodega.component.html',
  styleUrls: ['./add-productos-bodega.component.css']
})
export class AddProductosBodegaComponent {
  constructor(private servicioProductos:ProductosService){}
  mostrarNuevoIncluido:boolean=false
  mostrarNuevo:boolean=false
  productos!:Producto[]

  pasillobodega!:number
  idProductoIncluido!:number
  ngOnInit():void{
    this.servicioProductos.getProductosNoInlcuidos(this.getSucursal(),this.getRolDB()).subscribe(data=>{
      this.productos=data;
    })
  }

  getRolDB(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let rol = usuario? usuario.rol:0;
    return rol;
  }
  getSucursal(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let suc = usuario? usuario.sucursal:0;
    return suc;
  }

  dejarDeMostrarNuevoIncluido(){
    this.mostrarNuevoIncluido=false;
  }

  agregarNuevoIncluido(nuevoIncluidoForm:NgForm){
    let ps = new ProductosSucursales(this.idProductoIncluido,this.getSucursal(),0,0,this.pasillobodega,0);
    console.log(ps)
    this.servicioProductos.nuevoProductoSucursal(ps,this.getRolDB()).subscribe(data=>{
      alert("Se ha incluido el producto al sucursal");
      this.ngOnInit();
      nuevoIncluidoForm.resetForm();
      this.mostrarNuevoIncluido=false
    })

  }

  dejarDeMostrarNuevo(){

  }

  addIncluido(p:Producto){
    this.mostrarNuevoIncluido=true;
    this.idProductoIncluido=p.codigo;
  }

}


<div class="container mt-2 col-md-10" *ngIf="mostrarNuevoIncluido">
    <div class="row">
        <div class="col">
            <div class="card my-10">

                <div class="text-end">
                    <button type="submit" class="btn btn-primary"
                        (click)="dejarDeMostrarNuevoIncluido()"><i class="bi bi-add"></i> Aceptar</button>
                </div> 
                <div class="card-body">
                    <form (ngSubmit)="agregarNuevoIncluido(nuevoIncluidoForm)"  #nuevoIncluidoForm="ngForm">

                        <div class="form-group">
                            <label for="precio">Ingrese el pasillo en la bodega donde almacenara el producto {{idProductoIncluido}}</label>
                            <input type="number" class="form-control" name="pasillo" [(ngModel)]="pasillobodega"  required>
                        </div>                        
                        <br>
                        <div>
                            <button type="submit" class="btn btn-success"
                                [disabled]="!nuevoIncluidoForm.form.valid"><i class="bi bi-add"></i> Agregar</button>
                        </div> 
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>

<div class="container mt-5 pt-5" *ngIf="!mostrarNuevoIncluido && !mostrarNuevo" >
    <div class="card">
        <h3>Productos para agregar al sucursal</h3>
        <div class="text-end">
            <button type="submit" class="btn btn-primary me-3 mt-2"
                (click)="mostrarNuevo=true">
                <i class="bi bi-plus"></i> Agregar
            </button>
        </div>
        <br>

        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Codigo</th>
                    <th>Nombre</th>
                    <th>Precio de venta</th>
                    <th class="text-center">ACCIONES</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let p of productos; let i = index">
                    <td>{{p.codigo}}</td>
                    <td>{{p.nombre}}</td>
                    <td>{{p.precio_unitario_venta}}</td>
                    <td class="text-center">
                        <button class="btn btn-success me-2 col-sm-10" (click)="addIncluido(p)" >      Agregar a sucursal</button>
                    </td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
