import { Component } from '@angular/core';
import { Venta } from 'src/app/Modelo/Venta';
import { VentasService } from 'src/app/Service/ventas.service';
import { ProductosService } from 'src/app/Service/productos.service';
import { Producto } from 'src/app/Modelo/Producto';
import { Usuario } from 'src/app/Modelo/Usuario';
import { data, error } from 'jquery';
import { NgForm } from '@angular/forms';
import { ClientesService } from 'src/app/Service/clientes.service';
import { Cliente } from 'src/app/Modelo/Cliente';
import { ProdcutosVenta } from 'src/app/Modelo/ProductosVenta';
@Component({
  selector: 'app-ventas',
  templateUrl: './ventas.component.html',
  styleUrls: ['./ventas.component.css']
})
export class VentasComponent {

  constructor(private servicioVentas:VentasService, private servicioProductos:ProductosService,
    private servicioClientes:ClientesService
  ){}
  ngOnInit():void{
    this.servicioVentas.getVentas(this.getRolDB(),this.getSucursal()).subscribe(data=>{
      this.ventas=data;
      this.traerProductos();
      this.getClientes();
    },error=>{
      console.log(error)
      this.traerProductos();
      this.getClientes();
    })
  }

  productosNuevaVenta!:ProdcutosVenta[]

  totalNuevaVenta:number=0
  productos!:Producto[]
  mostrarDetalles:boolean=false
  mostrarNuevaVenta:boolean=false

  clientes!:Cliente[]

  ventas!:Venta[]
  precioVentaProducto!:number
  idProductoSeleccionado:any
  productoSeleccionado!:Producto
  idCliente!:number

  cantidadProducto!:number
  codigoNuevaVenta!:number


  verDetalles(v:Venta){
    console.log(v)
  }

  nuevaVenta(){
    //generar la nueva venta
    this.productosNuevaVenta=[]
    this.totalNuevaVenta=0;
    this.mostrarNuevaVenta=true;
    let v = new Venta(1,this.getIDUser(),"",this.getSucursal(),"",this.idCliente,"","",0,0,"",this.getCaja(),0)
    console.log(v);
    this.servicioVentas.NuevaVenta(v,this.getRolDB()).subscribe(data=>{
      this.codigoNuevaVenta=data.codido_venta
    })
  }
  mostrarInfoProducto(){
    this.productoSeleccionado=this.getProducto(this.productos,parseInt(this.idProductoSeleccionado));
  }
  dejarDeMostrarNuevaVenta(){
    this.mostrarNuevaVenta=false;
    if(this.totalNuevaVenta==0){
      //eliminar la venta
      this.ngOnInit();

    }else{
      this.ngOnInit();
    }
  }
  traerProductos(){
    this.servicioProductos.getProductosSucursal(this.getSucursal(),this.getRolDB()).subscribe(data=>{
      this.productos=data;
    }, error=>{
      console.log(error)
    })
  }
  agregarProductoAVenta(nuevaVentaForm:NgForm){
    nuevaVentaForm.resetForm();
    //insertar productos a la venta
  }

  getRolDB(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let rol = usuario? usuario.rol:0;
    return rol;
  }
  getCaja(){
    let stringUser = localStorage.getItem('caja');
    let caja:number = parseInt(stringUser? JSON.parse(stringUser):"");
    return caja;
  }
  getIDUser(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let rol = usuario? usuario.identificacion:0;
    return rol;
  }
  getSucursal(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let suc = usuario? usuario.sucursal:0;
    return suc;
  }

  getProducto(productos: Producto[], idp: number): Producto {
    const producto = productos.find(producto => producto.codigo === idp);
    if (!producto) {
      throw new Error(`Producto con ID ${idp} no encontrado`);
    }
    return producto;
  }

  getClientes(){
    this.servicioClientes.getClientes(this.getRolDB()).subscribe(data=>{
      this.clientes=data;
    }, error=>{
      console.log(error)
    })
  }



}


<div class="container mt-2 col-md-10" *ngIf="mostrarNuevaVenta">
    <div class="row">
        <div class="col">
            <div class="card my-10">
                <nav class="navbar navbar-expand-lg bg-white navbar-light py-3 py-lg-0 px-lg-5">
                    <h3 class="mt-2 me-4">Nueva Venta</h3>
                </nav>
                <div class="text-end">
                    <button type="submit" class="btn btn-primary"
                        (click)="dejarDeMostrarNuevaVenta()"><i class="bi bi-add"></i> Aceptar</button>
                </div> 
                <br>
                <div class="form-group">
                    <label for="rol">Seleccione el cliente:</label>
                    <select class="form-select" aria-label="Default select example" id="combo"  name="comboClientes" required [(ngModel)]="idCliente" >
                        <option *ngFor="let c of clientes; let i = index" [value]=c.identificacion>{{c.identificacion}}: {{c.nombre}}</option>
                    </select>
                </div><br>
                <div class="card-body">
                    <form (ngSubmit)="agregarProductoAVenta(nuevaVentaForm)"  #nuevaVentaForm="ngForm">

                        <div class="form-group">
                            <label for="rol">Seleccione el producto:</label>
                            <select class="form-select" aria-label="Default select example" id="combo"  name="comoboProductos" required [(ngModel)]="idProductoSeleccionado" (click)="mostrarInfoProducto()">
                                <option *ngFor="let pr of productos; let i = index" [value]="pr.codigo">C.{{pr.codigo}}:  {{pr.nombre}}</option>
                            </select>
                        </div>
                        <br>
                        <h4>Cantidad en estanteria: {{productoSeleccionado.cantidad_estanteria}}</h4><br>
                        <h4>Precio del producto: Q.{{productoSeleccionado.precio_unitario_venta}}</h4>
                        <div class="form-group">
                            <label for="cantidad">Ingrese la cantidad del producto:</label>
                            <input type="number" class="form-control" name="cantidad" [(ngModel)]="cantidadProducto"  required>
                        </div>                        
                        <br>
                        <div>
                            <button type="submit" class="btn btn-success"
                                [disabled]="!nuevaVentaForm.form.valid"><i class="bi bi-add"></i> Agregar</button>
                        </div> 
                    </form>
                </div>
                <h3>Productos en la venta {{codigoNuevaVenta}}</h3>
                <table class="table table-bordered">
                    <thead>
                        <tr>
                            <th>Compra</th>
                            <th>Nombre producto</th>
                            <th>Cantidad</th>
                            <th>Precio unitario</th>
                            <th>Subtotal</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let pc of productosNuevaCompra; let i = index">
                            <td>{{pc.id_compra}}</td>
                            <td>{{pc.producto}}</td>
                            <td>{{pc.cantidad}}</td>
                            <td>{{pc.precio_unitario}}</td>
                            <td>{{pc.subtotal}}</td>
                        </tr>

                    </tbody>
                </table>
                <h3>Precio total {{totalNuevaVenta}}</h3>
            </div>
        </div>
    </div>
</div>

<div class="container" *ngIf="!mostrarNuevaVenta && !mostrarDetalles" >
    <div class="card">
        <h3>Totales ({{ventas.length}}) </h3>
        <div class="text-end">
            <button class="btn btn-primary ms-3 mt-2" aria-label="Agregar compra" (click)="nuevaVenta()">
                <i class="bi bi-cart-plus-fill bi-xxl"></i> Agregar
            </button>
        </div><br>

        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Codigo</th>
                    <th>Cliente</th>
                    <th>Sucursal</th>
                    <th>Caja</th>
                    <th>Fecha</th>
                    <th>Total</th>
                    <th>T/C descuento</th>
                    <th class="text-center">ACCIONES</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let v of ventas; let i = index">
                    <td>{{v.codigo}}</td>
                    <td>{{v.n_cliente}}</td>
                    <td>{{v.n_sucursal}}</td>
                    <td>Caja {{v.caja}}</td>
                    <td>{{v.fecha}}</td>
                    <td>{{v.total_sin_descuento}}</td>
                    <td>{{v.total_con_descuento}}</td>
                    <td class="text-center">
                        <button class="btn btn-warning " (click)="verDetalles(v)"> <i class="bi bi-eye"></i>      Detalles</button>
                    </td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
