import { Component, OnInit } from '@angular/core';
import { Compra } from 'src/app/Modelo/Compra';
import { ComprasService } from 'src/app/Service/compras.service';
import { Usuario } from 'src/app/Modelo/Usuario';
import { data, error } from 'jquery';
import { Producto } from 'src/app/Modelo/Producto';
import { ProductosService } from 'src/app/Service/productos.service';
import { ProdcutosCompra } from 'src/app/Modelo/ProductosCompra';
import { NgForm } from '@angular/forms';
@Component({
  selector: 'app-compras',
  templateUrl: './compras.component.html',
  styleUrls: ['./compras.component.css']
})
export class ComprasComponent {
  constructor(private servicioCompras:ComprasService, private servicioProductos:ProductosService){}
  compras!:Compra[]
  mostrarNuevaCompra:boolean=false;
  productos!:Producto[]

  precioProducto!:number
  cantidadProducto!:number
  productoSeleccionado!:any

  codigoNuevaCompra!:number

  productosNuevaCompra !:ProdcutosCompra[]

  totalNuevaCompra:number=0;

  codigoDetalleCompra!:number
  mostrarDetalles:boolean=false;

  ngOnInit():void{
    this.servicioCompras.getCompras(this.getRolDB(),this.getSucursal()).subscribe(data=>{
      this.compras=data;
      this.traerProductos();
    }, error=>{
      console.log(error)
      this.traerProductos();
    })
  }
  verDetalles(compra:Compra){
    this.mostrarDetalles=true;
    this.servicioProductos.getProductosNuevaCompra(compra.codigo,this.getRolDB()).subscribe(data=>{
      this.productosNuevaCompra=data
      this.codigoDetalleCompra=compra.codigo
      this.totalNuevaCompra=compra.total_compra
    })
  }

  filtrarCompras(filtro:string){
    console.log("filtro: " + filtro)
  }

  getRolDB(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let rol = usuario? usuario.rol:0;
    return rol;
  }
  getIDUser(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let rol = usuario? usuario.identificacion:0;
    return rol;
  }
  getSucursal(){
    let stringUser = localStorage.getItem('usuario');
    let usuario:Usuario = stringUser? JSON.parse(stringUser):null;
    let suc = usuario? usuario.sucursal:0;
    return suc;
  }

  nuevaCompra(){
    //hacer que se agregue una nueva compra
    this.productosNuevaCompra= []
    this.totalNuevaCompra=0;
    this.mostrarNuevaCompra=true;
    let compra = new Compra();
    compra.usuario=this.getIDUser();
    compra.sucursal=this.getSucursal();
    this.servicioCompras.nuevaCompra(compra,this.getRolDB()).subscribe(data=>{
      this.codigoNuevaCompra=data.codigo_compra;
      console.log(this.codigoNuevaCompra)

    },error=>{
      console.log(error)
    })
  }

  traerProductosNuevaCompra(idC:number){
    this.servicioProductos.getProductosNuevaCompra(idC,this.getRolDB()).subscribe(data=>{
      this.productosNuevaCompra=data;
    },error=>{
      console.log(error)
    })
  }

  agregarProductoACompra(nuevaCompraForm:NgForm) {
    let pc = new ProdcutosCompra(this.codigoNuevaCompra,parseInt(this.productoSeleccionado),this.cantidadProducto,this.precioProducto);
    this.servicioProductos.nuevoProductoCompra(pc,this.getRolDB()).subscribe(data=>{
      console.log(data);
      this.traerProductosNuevaCompra(this.codigoNuevaCompra);
      nuevaCompraForm.resetForm();
      this.totalNuevaCompra+=pc.cantidad*pc.precio_unitario;
    }, error=>{
      console.log(error)
      alert("No cuentas con el dinero sufuciente para realizar esta compra, por favor comunicate con el administrador")
    })
  }

  traerProductos(){
    this.servicioProductos.getProductosSucursal(this.getSucursal(),this.getRolDB()).subscribe(data=>{
      this.productos=data;
    }, error=>{
      console.log(error)
    })
  }

  dejarDeMostrarNuevaCompra(){
    this.mostrarNuevaCompra=false;
    if(this.totalNuevaCompra==0){
      //eliminar la compra
      this.servicioCompras.deleteCompra(this.getRolDB(),this.codigoNuevaCompra).subscribe(data=>{
        console.log("compra eliminada");
        this.ngOnInit();
      })
    }else{
      this.ngOnInit();
    }
  }




}

<div class="container mt-2 col-md-10" *ngIf="mostrarDetalles">
    <div class="row">
        <div class="col">
            <div class="card my-10">
                <nav class="navbar navbar-expand-lg bg-white navbar-light py-3 py-lg-0 px-lg-5">
                    <h3 class="mt-2 me-4">Detalles en esta compra</h3>
                </nav>
                <div class="text-end">
                    <button type="submit" class="btn btn-primary"
                        (click)="mostrarDetalles=false"><i class="bi bi-add"></i> Aceptar</button>
                </div> 
                <div class="card-body">

                </div>
                <h3>Productos en la compra {{codigoDetalleCompra}}</h3>
                <table class="table table-bordered">
                    <thead>
                        <tr>
                            <th>Compra</th>
                            <th>Nombre producto</th>
                            <th>Cantidad</th>
                            <th>Precio unitario</th>
                            <th>Subtotal</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let pc of productosNuevaCompra; let i = index">
                            <td>{{pc.id_compra}}</td>
                            <td>{{pc.producto}}</td>
                            <td>{{pc.cantidad}}</td>
                            <td>{{pc.precio_unitario}}</td>
                            <td>{{pc.subtotal}}</td>
                        </tr>

                    </tbody>
                </table>
                <h3>Precio total {{totalNuevaCompra}}</h3>
            </div>
        </div>
    </div>
</div>


<div class="container mt-2 col-md-10" *ngIf="mostrarNuevaCompra">
    <div class="row">
        <div class="col">
            <div class="card my-10">
                <nav class="navbar navbar-expand-lg bg-white navbar-light py-3 py-lg-0 px-lg-5">
                    <h3 class="mt-2 me-4">Nueva Compra</h3>
                </nav>
                <div class="text-end">
                    <button type="submit" class="btn btn-primary"
                        (click)="dejarDeMostrarNuevaCompra()"><i class="bi bi-add"></i> Aceptar</button>
                </div> 
                <div class="card-body">
                    <form (ngSubmit)="agregarProductoACompra(nuevaCompraForm)"  #nuevaCompraForm="ngForm">

                        <div class="form-group">
                            <label for="rol">Seleccione el producto:</label>
                            <select class="form-select" aria-label="Default select example" id="combo"  name="comoboProductos" required [(ngModel)]="productoSeleccionado">
                                <option *ngFor="let pr of productos; let i = index" [value]="pr.codigo">C.{{pr.codigo}}:  {{pr.nombre}}, en bodega ({{pr.cantidad_bodega}})</option>
                            </select>
                        </div>
                        <br>
                        <div class="form-group">
                            <label for="cantidad">Ingrese la cantidad del producto:</label>
                            <input type="number" class="form-control" name="cantidad" [(ngModel)]="cantidadProducto"  required>
                        </div>                        
                        <br>
                        <div class="form-group">
                            <label for="precio">Ingrese el precio de compra del producto:</label>
                            <input type="number" class="form-control" name="precio" [(ngModel)]="precioProducto"  required>
                        </div>                        
                        <br>
                        <div>
                            <button type="submit" class="btn btn-success"
                                [disabled]="!nuevaCompraForm.form.valid"><i class="bi bi-add"></i> Agregar</button>
                        </div> 
                    </form>
                </div>
                <h3>Productos en la compra {{codigoNuevaCompra}}</h3>
                <table class="table table-bordered">
                    <thead>
                        <tr>
                            <th>Compra</th>
                            <th>Nombre producto</th>
                            <th>Cantidad</th>
                            <th>Precio unitario</th>
                            <th>Subtotal</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let pc of productosNuevaCompra; let i = index">
                            <td>{{pc.id_compra}}</td>
                            <td>{{pc.producto}}</td>
                            <td>{{pc.cantidad}}</td>
                            <td>{{pc.precio_unitario}}</td>
                            <td>{{pc.subtotal}}</td>
                        </tr>

                    </tbody>
                </table>
                <h3>Precio total {{totalNuevaCompra}}</h3>
            </div>
        </div>
    </div>
</div>

<div class="container mt-5 pt-5" *ngIf="!mostrarNuevaCompra && !mostrarDetalles" >
    <div class="card">
        <h3>Totales ({{compras.length}}) </h3>
        <div class="text-end">
            <button class="btn btn-primary ms-3 mt-2" aria-label="Agregar compra" (click)="nuevaCompra()">
                <i class="bi bi-cart-plus-fill bi-xxl"></i> Agregar
            </button>
        </div><br>

        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Codigo</th>
                    <th>Sucursal</th>
                    <th>Fecha de compra</th>
                    <th>Total</th>
                    <th class="text-center">ACCIONES</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let compra of compras; let i = index">
                    <td>{{compra.codigo}}</td>
                    <td>{{compra.sucursal}}</td>
                    <td>{{compra.fecha}}</td>
                    <td>{{compra.total_compra}}</td>
                    <td class="text-center">
                        <button class="btn btn-warning me-2 col-sm-5" (click)="verDetalles(compra)"> <i class="bi bi-eye"></i>      Detalles</button>
                    </td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
